function run(previousState, typeInput, previousId) {
    try {
        typeInput = JSON.parse(typeInput).input
        let idFormated = previousState.split(' ');
        idFormated = idFormated[0].replace(/\[/g,'').replace(/\]/g,'').replace(/\./g,'');
        const exception = {
            id: `${idFormated}`,
            input: '{{input.content}}',
            redirectMenu: `${typeInput}`,
            blockId: previousId
        }
        return exception
    }
    catch (e) {
        return 'error'
    }
}

function run(hideInputText, sentenceOtherMistakesJs) {

  let properties = {
    'text': 'Selecione qual {{n1}}problema{{n2}} você está passando clicando no {{n1}}menu{{n2}} abaixo:',
    'options': ["Áudio ruim", "Vídeo ruim", "Aula em falta", "PDF não encontrado", "Não salva as aulas",
      "Link não funciona", "Outros assuntos", "↩️ Menu principal"],
    'values': ["Áudio ruim", "Vídeo ruim", "Aula em falta", "PDF não encontrado", "Não salva as aulas",
      "Link não funciona", "Outros assuntos", "Menu principal"],
    'description': [],
    'itens': [
      { name: ['Áudio ruim'] },
      { name: ['Vídeo ruim'] },
      { name: ['Aula em falta'] },
      { name: ['PDF não encontrado'] },
      { name: ['Não salva as aulas'] },
      { name: ['Link não funciona'] },
      { name: ['Outros assuntos'] },
      { name: ['Menu principal', 'menu', 'menu principal'] }
    ]
  }

  ////////////////////////////////////////////////////////////////////////////////
  // Exibe mensagem de exceção no MENU
  if (hideInputText == 'true' || hideInputText == true) {
    properties.text = sentenceOtherMistakesJs
  } 
  ////////////////////////////////////////////////////////////////////////////////

  properties.itens = addItensNumeracao(properties.itens);

  // Adição de opções sem numeração
  let opcoesExtras = []

  opcoesExtras = addItens(opcoesExtras);

  for (let i = 0; i < opcoesExtras.length; i++) {
    properties.itens.push(opcoesExtras[i]);
  }

  return JSON.stringify(properties);
}

function addItens(itens) {
  let newItens = [];
  let newNames;
  let name;
  let match;
  for (let i = 0; i < itens.length; i++) {
    name = itens[i].name;
    newNames = []
    for (let y = 0; y < itens[i].name.length; y++) {
      match = name[y];
      newNames.push(match);
      // Adiciona uma variação lowerCase e UpperCase para cada opção do itens
      newNames.push(match.toLowerCase());
      newNames.push(match.toUpperCase());
      newItens[i] = { name: newNames };
    }
  }
  return newItens;
}

function addItensNumeracao(itens) {
  let newItens = [];
  let newNames;
  let name;
  let match;
  for (let i = 0; i < itens.length; i++) {
    name = itens[i].name;
    newNames = []
    for (let y = 0; y < itens[i].name.length; y++) {
      match = name[y];
      newNames.push(match);
      // Adiciona uma variação lowerCase e UpperCase para cada opção do itens
      newNames.push(match.toLowerCase());
      newNames.push(match.toUpperCase());
      newItens[i] = { name: newNames };
    }
    newNames.push(`${i + 1}`); // Acrescenta numeração automática
  }
  return newItens;
}

