const run = (platform, menu) => {
  let convertPlatform = platform.toLowerCase();
  menu = JSON.parse(menu);
  platform = platform.toLowerCase();

  if (platform == 'instagram' || platform == 'messenger') {
    const objectType = {
      'type': `text/plain`
    }
    return objectType
  };

  let default_msg = {
    "text": menu.text,
    "header": 'Menu',
    "body": menu.text,
    "footer": '',
    "button": 'Menu',
    "title": 'Menu',
    "namespaceTemplate": '',
    "nameTemplate": '',
    "options": menu.options,
    "values": menu.values,
    "submenu": [],
    "description": menu.description,
    "menuScope": {
      "whatsappButton": false,
      "whatsappList": true,
      "blipchatQuickReply": false,
      "blipchatMenu": true,
      "defaultText": false,
      "platform": convertPlatform
    },
  };

  var newMenu = getMenuForPlatform(default_msg);

  return newMenu;
};

function run(menu, platform, objMenu) {
  objMenu = JSON.parse(objMenu);
  platform = platform.toLowerCase();

  switch (platform) {
    case 'instagram':
      return geraMenuNumerico(menu);

    case 'messenger':
      return geraMenuNumerico(menu);

    default:
      return objMenu.content;
  }
}

function geraMenuNumerico(menu) {
  menu = JSON.parse(menu);

  let listaOptions = '\n\n';
  for (let i = 0; i < menu.options.length; i++) {
    listaOptions = listaOptions + (i + 1) + '. ' + menu.options[i] + '\n';
  }

  return menu.text + listaOptions;
}

