function run(listaPneus, quantidade, validaIcebreakers, input) {
	try {
		listaPneus = JSON.parse(listaPneus)
		quantidade = parseInt(quantidade)
		validaIcebreakers = JSON.parse(validaIcebreakers)

		let pneuEscolhido;
		let preco;

		if (validaIcebreakers.type == 'error') {
			pneuEscolhido = listaPneus.response.products[input - 1]
			preco = parseFloat(pneuEscolhido.price).toFixed(2)
		} else {
			if (validaIcebreakers.dados_identificados.desconto == true) {
				pneuEscolhido = listaPneus.response.products[0]
				preco = parseFloat(pneuEscolhido.price).toFixed(2)
				preco = (preco - validaIcebreakers.dados_identificados.valorDesconto)
			} else if (validaIcebreakers.dados_identificados.desconto == false) {
				pneuEscolhido = listaPneus.response.products[0]
				preco = parseFloat(pneuEscolhido.price).toFixed(2)
				preco = preco
			} else {
				pneuEscolhido = listaPneus.response.products[input - 1]
				preco = parseFloat(pneuEscolhido.price).toFixed(2)
			}
		}

		let precoTotal = (preco * quantidade).toFixed(2)
		let descontoAVista = 0.06 // 6% de desconto √† vista
		let precoDescontoAVista = (precoTotal * (1 - descontoAVista)).toFixed(2)
		precoTotal = precoTotal.toString().replace('.', ',')		
		precoDescontoAVista = precoDescontoAVista.toString().replace('.', ',')

		let text = `Voc√™ escolheu {{n1}}${pneuEscolhido.title}{{n2}}

{{n1}}Quantidade:{{n2}} ${quantidade}

{{n1}}R$ ${precoDescontoAVista} via Pix{{n2}}
R$ ${precoTotal} em 6x sem juros

‚ö†Ô∏è A montagem √© gratuita.  `

		return JSON.stringify({
			text: text,
			img: pneuEscolhido.featured_src,
			precoTotal: precoTotal,
			precoDescontoAVista: precoDescontoAVista,
			quantidade: quantidade,
			pneuEscolhido: pneuEscolhido
		})

	} catch (e) {
		return `ERRO SCRIPT: ${e}`
	}
}

