function run(ativarTransbordo) {
    try {
        if (
            ativarTransbordo == null ||
            ativarTransbordo == undefined ||
            ativarTransbordo.toUpperCase() != "SIM"
        ) {
            return false;
        }
        return true;

    } catch (e) {
        return false;
    }
}

const run = (response_attendance_availability, team_service) => {
    response_attendance_availability = JSON.parse(response_attendance_availability);

    let attendance = {
        "available": false,
        "tracking": "Nao"
    }
    let has_attendant = false;

    if(response_attendance_availability.resource && response_attendance_availability.resource.total >= 1){
        has_attendant = response_attendance_availability.resource.items.find((item) => 
            item.name.toLowerCase() == team_service.toLowerCase() && item.agentsOnline > 0) ? attendance.available = true : attendance.available = false
    }

    if(attendance.available){
        attendance.tracking = "Sim";
    }

    return attendance;
}

