        const erroMenuEspecial = 'ERRO DINAMICO';
        const validacoesInput = {
            data: false,
            email: false,
            cep: false,
            img: false,
            imgTxt: false,
            nome: false
        };
        // ========================================================================

        // Inicia o objeto que ser√° retornado pela fun√ß√£o
        let processedInput = {
            'type': null,
            'input': null,
            'validation': 'none'
        }

        // Verifica se o usu√°rio digitou SAIR ou MENU
