function run(hideInputText, sentenceOtherMistakesJs) {

  let properties = {
    'text': `{{n1}}Precisa de algo mais?{{n2}}`,
    'options': ["N√£o preciso", "Falar com atendente"],
    'values': ["N√£o preciso", "Falar com atendente"],
    'description': [],
    'itens': [
      { name: ['N√£o preciso', 'nao', 'no', 'n', 'nn', 'nope'] },
      { name: ['Falar com atendente', 'atendente'] },
    ]
  }

    ////////////////////////////////////////////////////////////////////////////////
  // Exibe mensagem de exce√ß√£o no MENU
  if (hideInputText == 'true' || hideInputText == true) {
    properties.text = sentenceOtherMistakesJs + '\n\n{{n1}}Precisa de algo mais?{{n2}}'
  } 
  ////////////////////////////////////////////////////////////////////////////////

  properties.itens = addItensNumeracao(properties.itens);

  // Adi√ß√£o de op√ß√µes sem numera√ß√£o
  let opcoesExtras = []

  opcoesExtras = addItens(opcoesExtras);

  for (let i = 0; i < opcoesExtras.length; i++) {
    properties.itens.push(opcoesExtras[i]);
  }

  return JSON.stringify(properties);
}

function addItens(itens) {
  let newItens = [];
  let newNames;
  let name;
  let match;
  for (let i = 0; i < itens.length; i++) {
    name = itens[i].name;
    newNames = []
    for (let y = 0; y < itens[i].name.length; y++) {
      match = name[y];
      newNames.push(match);
      // Adiciona uma varia√ß√£o lowerCase e UpperCase para cada op√ß√£o do itens
      newNames.push(match.toLowerCase());
      newNames.push(match.toUpperCase());
      newItens[i] = { name: newNames };
    }
  }
  return newItens;
}

function addItensNumeracao(itens) {
  let newItens = [];
  let newNames;
  let name;
  let match;
  for (let i = 0; i < itens.length; i++) {
    name = itens[i].name;
    newNames = []
    for (let y = 0; y < itens[i].name.length; y++) {
      match = name[y];
      newNames.push(match);
      // Adiciona uma varia√ß√£o lowerCase e UpperCase para cada op√ß√£o do itens
      newNames.push(match.toLowerCase());
      newNames.push(match.toUpperCase());
      newItens[i] = { name: newNames };
    }
    newNames.push(`${i + 1}`); // Acrescenta numera√ß√£o autom√°tica
  }
  return newItens;
}

