function geraMenuNumerico(menu) {
  menu = JSON.parse(menu);

  let listaOptions = '\n\n';
  for (let i = 0; i < menu.options.length; i++) {
    listaOptions = listaOptions + (i + 1) + '. ' + menu.options[i] + '\n';
  }

  return menu.text + listaOptions;
}

