  if (hideInputText == 'true' || hideInputText == true) {
    properties.text = sentenceOtherMistakesJs + '\n\nO prazo de cancelamento √© de at√© {{n1}}07 dias{{n2}} ap√≥s a data da compra.\n\n{{n1}}Sua compra j√° passou de 7 dias ou ainda est√° dentro do prazo?{{n2}}'
  } 
  ////////////////////////////////////////////////////////////////////////////////

  properties.itens = addItensNumeracao(properties.itens);

  // Adi√ß√£o de op√ß√µes sem numera√ß√£o
  let opcoesExtras = []

  opcoesExtras = addItens(opcoesExtras);

