function run(countError, previousException, exception) {
    try {
        countError = Number(countError)
        previousException = JSON.parse(previousException)
        exception = JSON.parse(exception)

        if(countError > 2){
            return countError = 0
        }
        if (previousException.id == exception.id) {
            return countError += 1
        }

        return countError = 1
    }
    catch (e) {
        return 'error'
    }
}

