function run(input, inputType, platform, menu) {
    try {

        // =================================================================================
        //             ATENÃ‡ÃƒO: MANIPULAR SOMENTE AS DUAS CONSTANTES A SEGUIR
        // =================================================================================
        // - validacaoMenu => true para validaÃ§Ã£o de Menu, false para Input
        // - validacoesInput => insira aqui que tipo de validaÃ§Ã£o de input vai ser  
        //   necessÃ¡rio (se mais de um for selecionado, apenas o mais acima serÃ¡ validado)
        // - erroMenuEspecial => altere somente se, caso a validaÃ§Ã£o seja um Menu e para
        //   este menu haja um tratamento diferente de exceÃ§Ã£o.
        // =================================================================================
        const validacaoMenu = true;
        const erroMenuEspecial = 'ERRO DINAMICO';
        const validacoesInput = {
            primeiroNome: false,
            nomeCompleto: false,
            email: false,
            telefone: false,
            cep: false,
            data: false,
            img: false,
            imgTxt: false,
            endereco: false,
            moeda: false,
            cpf: false,
            cnpj: false,
            cpfcnpj: false
        };
        // ========================================================================

        // Inicia o objeto que serÃ¡ retornado pela funÃ§Ã£o
        let processedInput = {
            'type': null,
            'input': null,
            'validation': 'none'
        }

        // Verifica se o usuÃ¡rio digitou SAIR ou MENU
        if (input == 'sair' || input == 'SAIR' || input == 'Sair') {
            processedInput.type = 'reserved'
            processedInput.input = 'SAIR'
            return JSON.stringify(processedInput)
        } else if (input == 'voltar' || input == 'VOLTAR' || input == 'Menu' || input == 'Voltar' || input == 'menu' || input == 'MENU') {
            processedInput.type = 'reserved'
            processedInput.input = 'VOLTAR'
            return JSON.stringify(processedInput)
        }

        // Formata variÃ¡veis
        menu = JSON.parse(menu);
        platform = platform.toUpperCase()

        if (validacaoMenu) {
            // ValidaÃ§Ã£o de MENU
            processedInput = validaMenu(input, menu, platform)

            // Verifica se precisa mudar a mensagem de erro do Menu DinÃ¢mico para o Especial
            if ((processedInput.input == 'ERRO DINAMICO' || processedInput.input == 'ERRO NUMERICO') && erroMenuEspecial != '') {
                processedInput.input = erroMenuEspecial;
            }
            return JSON.stringify(processedInput);

        } else {
            // ValidaÃ§Ã£o de INPUT
            const val = Object.entries(validacoesInput);
            processedInput = validaInput(val, input, inputType)

            // Se nenhuma validaÃ§Ã£o foi processada, retorna o input sem validaÃ§Ã£o
            if (processedInput.input == 'INPUT SEM VALIDAÃ‡Ã•ES') {
                return JSON.stringify({ type: 'success', input: input, validation: 'none' });

            } else {
                return JSON.stringify(processedInput);
            }
        }

    } catch (e) {
        return { type: 'error', input: 'ERRO INESPERADO', validation: 'none' }
    }
}

